import { useState } from "react";
import "./App.css";
import { Styled } from "styled-components/dist/constructors/constructWithOptions";
import CustomerForm from "./components/CustomerForm/CustomerForm";
import CustomerList from "./components/CustomerList/CustomerList";

const AppWrapper=Styled.div`
.App {
  padding: 30px;
  background-color: white;
  border-radius: 10px;
}

.App h1{
  margin-bottom: 20px;
}

`

function App() {
  const [customers, setCustomers] = useState([]);

  const addNewCustomer = (newCustomer) => {
    // setCustomers([...customers, newCustomer]);
    setCustomers((prevState) => [newCustomer, ...prevState]);
  };
  return (
    <div className="App">
      <h1>Customer Manage System</h1>
      <CustomerForm addNewCustomer={addNewCustomer} />
      {customers.length === 0 && "There are no customers..."}
      <CustomerList customers={customers} setCustomers={setCustomers} />
    </div>
  );
}

export default App;
