// import "./CustomerItem.css"

import styles from "./CustomerItem.module.css"

// import styled from "styled-components"

// const DeleteButton = styled.button`
// background-color: red;
// padding: 8px;
// transition: opacity .5s ease;

// &:hover{opacity:0.5;}
// ` 

const CustomerItem = ({ customer, handleDelete }) => {

  console.log(styles)

  return (
    <li className={styles["customer-item"]}>
      <div className={styles["customer-info"]}>
        <img
          src="https://i.pravatar.cc/300"
          alt=""
          className="customer-avatar"
        />
        <span className={styles["customer-name"]} style={{
          color:"red",
          background:"blue"
        }}>{customer.customerName}</span>
      </div>
      <button className={styles["delete-button"]} onClick={() => handleDelete(customer)}>
        <i className="bi bi-trash3"></i>
      </button>
    </li>
  );
};

export default CustomerItem;
